package com.harsha.vizagitiee;

import android.location.Location;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import java.util.ArrayList;
import java.util.List;


public class BeachesFragment extends Fragment {
    private RecyclerView recyclerView;
    private BeachAdopter adapter;
    private List<Beaches> BeachList;
    private Location userLocation;
    public BeachesFragment() {
        // Required empty public constructor
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_beaches, container, false);
        recyclerView = view.findViewById(R.id.recycler_view);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));

        if (getArguments() != null) {
            double latitude = getArguments().getDouble("latitude");
            double longitude = getArguments().getDouble("longitude");
            userLocation = new Location("");
            userLocation.setLatitude(latitude);
            userLocation.setLongitude(longitude);
        }

        BeachList = new ArrayList<>();
        populateBeachList();

        adapter = new BeachAdopter(BeachList, userLocation);
        recyclerView.setAdapter(adapter);

        return view;
    }

    private void populateBeachList() {
        BeachList.add(new Beaches("The beach boasts soft, golden sand and clear waters, perfect for relaxing walks or picnicking with loved ones. The lush green hills surrounding the beach create a breathtaking backdrop, making it an idyllic spot to unwind and soak in the natural beauty.","Yarada Beach", "Yarada", 17.6549, 83.2692, "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4e/YARADA_BEACH_VIEW.jpg/640px-YARADA_BEACH_VIEW.jpg"));
        BeachList.add(new Beaches("Visakhapatnam's RK Beach, named after a nearby Ramakrishna Mission, is a bustling beach paradise. Explore the beach road shops or grab a delicious bite with a scenic view.\n" +
                "\n","R.K Beach", "Visakhapatnam", 17.7142, 83.3237, "https://i.pinimg.com/originals/1c/df/2f/1cdf2f28dfbb974cbe0b1161800c35aa.jpg"));
        BeachList.add(new Beaches("Rushikonda Beach is a paradise for nature lovers. Pristine golden sands and clear waters meet lush green hills. Unlike RK Beach, swimming is not ideal due to currents, but adventure seekers can enjoy water sports like surfing,jet skiing and scuba diving","Rushikonda Beach ", "Visakhapatnam", 17.7825, 83.3851, "data:image/jpeg;base64,"));
        BeachList.add(new Beaches("The park was named after Tenetti Viswanadham, a freedom fighter and social reformer. It features lush greenery, gardens, walking paths, and a children's play area, making it a popular spot for picnics, family outings, and photography.","Tenenti Beach", "Visakhapatnam", 17.7142, 83.3237, "data:image/jpeg;base64,"));
        BeachList.add(new Beaches("Bheemili Beach, a historical gem 24 kilometers from Visakhapatnam, offers a tranquil escape with calm waves lapping at the shore. Explore the remnants of Dutch settlements reflected in its colorful architecture.","Bheemili Beach", "Bheemili", 17.8903, 83.4559, "https://lh5.googleusercontent.com/p/AF1QipNTeLCB7khQN1MshJINfm2UJ-FB2wnNyU4M8otv=s296-w296-h168-n-k-no-v1"));
        BeachList.add(new Beaches("Mutyalampalem Beach is a hidden gem, known for its serenity and scenic backdrop.The anchored boats add a touch of charm, making it a perfect spot for peaceful walks, meditation.","MutyalamPalem Beach", "Parwada", 17.5364, 83.0906, "https://content.jdmagicbox.com/comp/thrissur/u7/9999px487.x487.190817055921.m9u7/catalogue/mutyalammapalem-beach-thrissur-beach-resorts-kasb7ftsgj.jpg"));
        BeachList.add(new Beaches("Escape the crowds at Visakhapatnam's hidden gem, Seethapalem Beach. This secluded haven boasts pristine sand, calm waters, and dramatic cliffs rumored to hide a secret cave." ,"Seetampalem  Beach", "Anakapalle", 17.4751, 82.9938, "https://miro.medium.com/v2/resize:fit:1400/1*wlTRC4LG2PfJHRtf4CS8tQ.jpeg"));
        BeachList.add(new Beaches("Visakhapatnam's Revupolavaram Beach offers a hidden charm. Escape the crowds and unwind on its golden sands, perfect for picnics and leisurely walks. A natural cliff divides the beach, creating a scenic vista.","Revupolavaram Beach", "Chinna Upalam", 17.3956, 82.8180, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTqc9_LIGHj_qkrKSGOoNdwV6RwUSzAaQo66Q&s"));
        BeachList.add(new Beaches("Tantadi Beach, also known as NTPC Pump Beach, offers a unique escape. Embrace the wild beauty of its black sand shores, a stark contrast to the usual golden beaches.","Tantadi Beach", "Parwada", 17.5255, 83.0657, "data:image/jpeg;base64,"));
        BeachList.add(new Beaches("Visakhapatnam's Divis Beach is a scenic getaway known for the iconic Divis Bridge towering over its golden sands. While swimming might be risky due to strong currents, the beach offers stunning photo opportunities and a chance to relax.","Divis Beach", "Bheemili", 17.9300, 83.4954, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTdmDAUltkWS-sIKOsKAesUuK-6WwmhubJJbg&s"));
    }

}

