package com.harsha.vizagitiee;

import androidx.appcompat.app.AppCompatActivity;
import androidx.core.content.ContextCompat;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.Window;
import android.view.WindowManager;

import java.util.ArrayList;
import java.util.List;

public class Food extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_food);
        Window window = this.getWindow();
        window.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS);
        window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS);
        window.setStatusBarColor(ContextCompat.getColor(this, R.color.white));
        RecyclerView recyclerView = findViewById(R.id.recyclerView);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

        List<FoodItem> foodList = new ArrayList<>();
        // Add sample data
        foodList.add(new FoodItem("Moori Mixture", "In Vizag, you can find delicious Muri Mixture at Jagadamba Junction, where vendors prepare it fresh with a blend of spices and crunchy ingredients. Another popular spot is RK Beach, where the mixture is enjoyed with the scenic view of the coastline.","R.K.Beach","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQFA8GPg6vIdxAUvdnvJ10hczMbAdu6acxDHg&s"));
        foodList.add(new FoodItem("Punugulu", "Punugulu is a popular street food snack from Andhra Pradesh, particularly in Vizag. These are crispy, deep-fried dumplings made from a batter of fermented rice and urad dal, similar to dosa or idli batter.Popular spots are Sri Sai Ram Parlour(Dwaraka Nagar) and so on.","Vizag","https://indianwanderers.wordpress.com/wp-content/uploads/2014/11/punugulu-cuisine-visakhapatnam.jpg?w=637&h=477"));
        foodList.add(new FoodItem("Bamboo Chicken", "Bamboo Chicken, or Bongulo Chicken, is a unique and flavorful delicacy in Vizag where marinated chicken is cooked inside bamboo stalks over an open fire, infusing it with a distinct smoky taste.","Vizag","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRtVuoGpLoCXd5gYBa7aVb6Cs9JzXV6tFz_wg&s"));
        foodList.add(new FoodItem("Royala Vepudu", "Royyala Vepudu in Vizag is a spicy and flavorful prawn fry that is a popular seafood dish, known for its aromatic blend of spices and crispy texture.","Vizag","https://www.indianhealthyrecipes.com/wp-content/uploads/2014/01/royyala-iguru.jpg.webp"));
        foodList.add(new FoodItem("Chepala Pulusu", "Chepala Pulusu in Vizag is a tangy and spicy fish curry made with tamarind and a blend of aromatic spices, celebrated for its rich flavors in the coastal city's cuisine.","Vizag Coastal line and Vizag","https://vismaifood.com/storage/app/uploads/public/c0d/434/20d/thumb__700_0_0_0_auto.jpg"));
        foodList.add(new FoodItem("Andhra Bhojanam", "Andhra Bhojanam is a traditional Andhra meal served on a banana leaf, featuring a variety of flavorful dishes like pappu (dal), pachadi (chutney), pulusu (sour curry), and pickles, offering a comprehensive taste of Andhra cuisine.","Vizag","data:image/jpeg;base64,"));
        foodList.add(new FoodItem("Bobatlu", "In Vizag, Bobbatlu is a cherished festive sweet, featuring thin flatbreads stuffed with a delightful mix of jaggery and chana dal, enjoyed especially during celebrations.","Vizag","https://www.orderyourchoice.com/124556-medium_default/bobbatlu.jpg"));
        foodList.add(new FoodItem("Kodi Kura Chiti garelu", "In Vizag and telugu States, a delightful combination is enjoying the spicy and flavorful Kodi Kora (chicken curry) alongside crispy Garelu (lentil fritters), offering a perfect blend of rich curry and crunchy snack. ","Vizag","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR2b3wWYBcPDDvH6dRefhY8TLS6WpCbzIuuOp1A-Azok3kLi9JSCEcqXFc_DaCupBuTpd0&usqp=CAU"));
foodList.add(new FoodItem("Pulihora","In Vizag and teluguStates, Pulihora is a tangy and spicy tamarind rice dish that is a beloved part of festive meals and special occasions."," Vizag Temples","https://i0.wp.com/cookingfromheart.com/wp-content/uploads/2016/07/Pulihora-5.jpg?resize=533%2C800&ssl=1"));

        FoodAdapter adapter = new FoodAdapter(foodList);
        recyclerView.setAdapter(adapter);
    }
}
