package com.harsha.vizagitiee;

import android.location.Location;
import android.os.Bundle;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import java.util.ArrayList;
import java.util.List;

public class NatureFragment extends Fragment {
    private RecyclerView recyclerView;
    private NatureAdopter adapter;
    private List<Nature> NatureList;
    private Location userLocation;
    public NatureFragment() {
        // Required empty public constructor
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_nature, container, false);
        recyclerView = view.findViewById(R.id.recycler_view);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));

        if (getArguments() != null) {
            double latitude = getArguments().getDouble("latitude");
            double longitude = getArguments().getDouble("longitude");
            userLocation = new Location("");
            userLocation.setLatitude(latitude);
            userLocation.setLongitude(longitude);
        }

        NatureList = new ArrayList<>();
        populateBeachList();

        adapter = new NatureAdopter(NatureList, userLocation);
        recyclerView.setAdapter(adapter);

        return view;
    }

    private void populateBeachList() {
        NatureList.add(new Nature("Visakhapatnam's Borra Caves are a spelunker's dream! Explore 150-million-year-old limestone formations sculpted by the Gosthani River.Marvel at stalactites and stalagmites resembling a Shivalinga, Mother & Child, and even an elephant's trunk! " ,"Borra Caves", "Aruku", 18.2807, 83.0397, "https://aptourism.gov.in/media-data/images/127/Borra-caves-gallery-(3)-720-960.jpg"));
        NatureList.add(new Nature("Visakhapatnam's Kailasagiri is a scenic hilltop park offering panoramic city views and a breath of fresh air. Hike or take the ropeway to the top and explore the lush greenery, spot the giant Shiva-Parvati statue, or unwind in the serene atmosphere. ","Kailasagiri", "Visakhapatnam", 17.7489, 83.3422, "https://vizagtourism.org.in/images/places-to-visit/header/kailasagiri-park-vizag-tourism-entry-fee-timings-holidays-reviews-header.jpg"));
        NatureList.add(new Nature("It is a hidden gem for nature lovers, offering a chance to escape the city and explore a beautiful dry evergreen forest.It is home to a variety of wildlife including barking deers and bird species.","Kambalakonda Wildlife Santuary", "Visakhapatnam", 17.34, 83.04, "https://th-i.thgim.com/public/incoming/5hyvjl/article65323303.ece/alternates/FREE_1200/Kambal02.jpg"));
        NatureList.add(new Nature("Visakhapatnam's Biodiversity Park, also known as the Botanical Garden, is a must-visit for nature enthusiasts. This 3-acre park, established in 2002, is a haven for over 2,000 species of plants and trees, many of them rare and endangered.","BioDiversity Park", "Visakhapatnam", 17.7289, 83.3360, "data:image/jpeg;base64,"));
        NatureList.add(new Nature("It is also known as Bahubali Waterfalls.Cascading down from a height of 60 meters amidst lush greenery and rocky cliffs, this waterfall transforms into a series of captivating cascades during the monsoon season","Devarapalli WaterFalls", "Anandapuram", 18.2807, 83.0397, "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQYp7P8aRsUwd1asDfKWQaqTLczNkf1Xq64Ug&s"));
        NatureList.add(new Nature(" Kothapalli Waterfalls, awaits explorers 30 kilometers from Paderu. Cascading down hills amidst serene natural beauty, these falls come alive during the monsoon (July-September).gzz","Kothapalli Waterfalls", "Gangaraju Madugala", 17.9899, 82.4790, "https://scontent.fhyd14-1.fna.fbcdn.net/v/t1.6435-9/78513576_727761101048941_957920394411507712_n.jpg?stp=dst-jpg_p526x296&_nc_cat=103&ccb=1-7&_nc_sid=7b2446&_nc_ohc=9LXtrn79jT8Q7kNvgF_ofrM&_nc_ht=scontent.fhyd14-1.fna&oh=00_AYBB64OzAOsFTdHvh9X0JpozCvL2uhLtoVSFZrUW4tAuRQ&oe=66B82D99"));
        NatureList.add(new Nature("This sanctuary boasts one of India's largest freshwater lakes, Kondakarla Ava, surrounded by scenic hills and coconut groves. Birdwatchers delight in the diverse species like Grey Herons and Pheasant-tailed Jacanas.","Kondakarla Ava Bird Santuary", "Aruku", 17.3603, 82.5953, "https://vizagtourism.org.in/images/places-to-visit/header/kondakarla-ava-beach-vizag-tourism-entry-fee-timings-holidays-reviews-header.jpg"));
        NatureList.add(new Nature("Indira Gandhi Zoological Park is a popular tourist destination, sprawling over 625 acres and home to nearly 800 species of animals. Established in 1972, the zoo offers visitors a chance to see a variety of animals from different parts of the world.","Zoo Park", "Visakhapatnam", 17.7606, 83.3549, "https://vizagtourism.org.in/images/places-to-visit/header/indira-gandhi-zoological-park-vizag-tourism-entry-fee-timings-holidays-reviews-header.jpg"));
        NatureList.add(new Nature(
                "Nestled amidst the picturesque Eastern Ghats in Andhra Pradesh, Lambasingi is a tiny hamlet often referred to as the Kashmir of Andhra Pradesh.Its average temperatures hovering below 10 degrees Celsius, in stark contrast to the usual hot and humid climate of the state. ","Lambasingi", "Aruku", 17.8186, 82.4922, "https://www.trawell.in/admin/images/upload/025326672Lambasingi-viewpoint.jpg"));

    }
}
